mapper.select:
SELECT * FROM ( SELECT r.rewardName AS rewardName, SUM(bd.quantity) AS qty, SUM(bd.quantity * bd.price) AS revenue FROM payment pay JOIN backing b ON b.backingId = pay.backingId JOIN backingDetail bd ON bd.backingId = b.backingId JOIN reward r ON r.rewardId = bd.rewardId WHERE pay.status = 'PAID'
AND pay.createdAt >= #{from, jdbcType=TIMESTAMP}
AND pay.createdAt < #{to, jdbcType=TIMESTAMP}
GROUP BY r.rewardId, r.rewardName
ORDER BY revenue DESC, qty DESC ORDER BY qty DESC, revenue DESC
)
WHERE ROWNUM <= #{limit}

SELECT method AS paymentMethod, COUNT(*) AS cnt
FROM payment
WHERE status = 'PAID'

    AND createdAt >= #{from,jdbcType=TIMESTAMP}

        AND createdAt < #{to, jdbcType=TIMESTAMP}


GROUP BY method

sql.where:
p.PROJECTSTATUS = 'UNDER_REVIEW'
AND p.ISREQREVIEW = 'Y'

    AND p.REQUESTEDAT  >=  #{dto.fromDate}


    AND p.REQUESTEDAT  <  #{dto.toDateEndExclusive}
