mapper.update:
UPDATE backing


        addrId = (
            SELECT addrId
            FROM address
            WHERE roadAddr =#{newAddress.roadAddr}
            AND detailAddr =#{newAddress.detailAddr}
        FETCH FIRST 1 ROWS ONLY
        )


        addrId = addrId


WHERE backingId = #{backingId}
AND userId = #{userId}

mapper.select:
SELECT backingid, userid, payinfoid, amount, createdat, backingstatus
FROM backing
WHERE backingId = #{backingId}
