mapper.sql:
WITH base AS ( SELECT /*+ MATERIALIZE */ CMID, USERID, PROJECTID, CMCONTENT, NVL(RATING, 0) AS RATING, CREATEDAT, CODE FROM ( SELECT CMID, USERID, PROJECTID, CMCONTENT, NVL(RATING, 0) AS RATING, CREATEDAT, CODE FROM COMMUNITY WHERE PROJECTID = #{projectId} AND TRIM(CODE) = #{code}
AND ( CREATEDAT < #{lastCreatedAt} OR (CREATEDAT = #{lastCreatedAt} AND CMID < #{lastId}) )
ORDER BY CREATEDAT DESC, CMID DESC ) WHERE ROWNUM <= #{size} ) SELECT b.CMID AS cmId, b.PROJECTID AS projectId, b.CMCONTENT AS cmContent, b.CREATEDAT AS createdAt, b.CODE AS code, b.RATING AS rating, u.NICKNAME AS nickname, u.PROFILEIMG AS profileImg FROM base b LEFT JOIN USERS u ON u.USERID = b.USERID ORDER BY b.CREATEDAT DESC, b.CMID DESC
