mapper.select:
SELECT * FROM
(SELECT sub.*, ROWNUM rnum FROM (
SELECT * FROM inquiry order by INQID desc
) sub WHERE ROWNUM  <=  #{pager.endRow})
WHERE rnum  >=  #{pager.startRow}

SELECT * FROM
(SELECT sub.*, ROWNUM rnum FROM (
SELECT * FROM inquiry WHERE USERID = #{userId} order by INQID desc
) sub WHERE ROWNUM  <=  #{pager.endRow})
WHERE rnum  >=  #{pager.startRow}
