mapper.select:
SELECT rewardid, projectid, rewardname, price, rewardcontent, rewardcnt, remain, isposting, deliverydate, createdAt
FROM REWARD
WHERE PROJECTID = #{projectId}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM REWARD
WHERE PROJECTID = #{projectId}
AND LOWER(REGEXP_REPLACE(TRIM(REWARDNAME), '\s+', ' ')) = #{normalizedName}
AND NVL(DEL_YN,'N') = 'N'

SELECT rewardid, projectid, rewardname, price, rewardcontent, rewardcnt, remain, isposting, deliverydate, createdAt
FROM REWARD
WHERE REWARDID = #{rewardId}

mapper.update:
update reward
set remain = case
                when remain is not null and remain >= #{quantity}
                then remain - #{quantity}
                else remain
            end
where rewardId = #{rewardId}
AND remain is not null

UPDATE reward
SET remain = NVL(remain, 0) + #{quantity}
WHERE rewardId = #{rewardId}
AND remain is not null

mapper.select:
select projectId
from reward
where rewardId = #{rewardId}

select rewardId, quantity
from backingDetail
where backingId = #{backingId}
